function o(i,{Plot:t,d3:l,width:r}={}){const a=l.max(i,e=>e.gameweek),n=i.filter(e=>e.gameweek>a-5);return t.plot({title:"Last 5 Gameweeks Form",subtitle:"Position finished each gameweek. 1 = First place, 4 = Last place.",width:r,height:180,marginLeft:120,marginRight:60,x:{label:"Gameweek",tickFormat:e=>`GW${e}`,domain:l.range(a-4,a+1),padding:0},y:{label:null,padding:0},color:{type:"ordinal",domain:[1,2,3,4],range:["#10b981","#fbbf24","#f97316","#ef4444"],legend:!0,label:"Position"},marks:[t.cell(n,{x:"gameweek",y:"team",fill:"rank",tip:!0,inset:0}),t.text(n,{x:"gameweek",y:"team",text:e=>e.rank===1?"1st":e.rank===2?"2nd":e.rank===3?"3rd":"4th",fill:"white",fontWeight:"bold",fontSize:11})]})}export{o as formChart};
